                        }}
                    >
                        {item.category}
                    </span>
                </div>
            )}

            {/* Primary action */}
            {(item.link || item.route) && (
                <button
                    onClick={handlePrimaryAction}
                    className="cursor-pointer self-start px-6 py-3"
                    style={{
                        fontFamily: "source-code-pro, Menlo, Monaco, Consolas, monospace",
                        fontSize: "12px",
                        color: "var(--bg-primary)",
                        backgroundColor: accentColor,
                        border: "none",
                        borderRadius: "4px",
                        letterSpacing: "0.05em",
                        transition: "opacity 0.15s ease",
                    }}
                    onMouseEnter={(e) => e.currentTarget.style.opacity = "0.85"}
                    onMouseLeave={(e) => e.currentTarget.style.opacity = "1"}
                >
                    {actionText}
                </button>
            )}

            {/* Spacer */}
            <div className="flex-1" />

            {/* Footer */}
            <section
                className="pt-6"
                style={{ borderTop: "1px solid var(--border-subtle)" }}
            >
                <div
                    className="flex justify-center gap-6"
                    style={{
                        fontFamily: "source-code-pro, Menlo, Monaco, Consolas, monospace",
                        fontSize: "11px",
                    }}
                >
                    <Link
                        to="/cv"
                        className="underline underline-offset-4"
                        style={{ color: "var(--text-muted)" }}
                    >
                        CV
                    </Link>
                </div>
            </section>
        </div>
    );
}

import React, { useState, useMemo, useEffect } from "react";
import { hexagrams, getHexagramLines } from "../data/hexagrams";
import { getDailyHexagramNumber } from "../data/siteContent";

/**
 * InstrumentBackdrop - Minimalist hexagram display (no rings)
 * 
 * Features:
 * - Large, centered hexagram
 * - Line-by-line reveal with accent pulse
 * - Clean hover metadata
 */

// === CONFIGURATION ===
const INSTRUMENT_SCALE = 1.8;
const DEFAULT_OPACITY = 0.2;
const HOVER_OPACITY = 0.45;
const FOCUS_OPACITY = 0.6;

// Animation timing (ms)
const LINE_REVEAL_DELAY = 180;
const LINE_REVEAL_DURATION = 220;
const PULSE_DURATION = 350;

// Geometry
const HEXAGRAM_WIDTH = 80;
const SVG_SIZE = 200;

export default function InstrumentBackdrop({ onFocusChange }) {
    const [isHovered, setIsHovered] = useState(false);
    const [isFocused, setIsFocused] = useState(false);
    const [visibleLines, setVisibleLines] = useState(0);
    const [pulsingLine, setPulsingLine] = useState(-1);

    // Check for reduced motion preference
    const prefersReducedMotion = useMemo(() => {
        if (typeof window === "undefined") return false;
        return window.matchMedia("(prefers-reduced-motion: reduce)").matches;
    }, []);

    // Daily hexagram
    const hexagramNumber = useMemo(() => getDailyHexagramNumber(), []);
    const accentColor = hexagramNumber % 2 === 0 ? "var(--accent-green)" : "var(--accent-pink)";
    const hexagram = hexagrams.find((h) => h.number === hexagramNumber) || hexagrams[0];
    const lines = getHexagramLines(hexagramNumber);

    const center = SVG_SIZE / 2;

    // Line-by-line reveal with per-line accent pulse
    useEffect(() => {
        if (prefersReducedMotion) {
            setVisibleLines(6);
            return;
        }

        const timers = [];

        for (let i = 0; i < 6; i++) {
            const revealTime = i * LINE_REVEAL_DELAY;

            timers.push(setTimeout(() => {
                setVisibleLines(i + 1);
                setPulsingLine(i);
            }, revealTime));

            timers.push(setTimeout(() => {
                setPulsingLine((current) => current === i ? -1 : current);
            }, revealTime + PULSE_DURATION));
        }

        return () => timers.forEach(clearTimeout);
    }, [prefersReducedMotion]);

    // Handle click to toggle focus
    const handleClick = () => {
        const newFocused = !isFocused;
        setIsFocused(newFocused);
        if (onFocusChange) onFocusChange(newFocused);
    };

    // Render hexagram line with pulse support
    const renderHexagramLine = (isYang, index) => {
        const lineHeight = 6;
        const lineGap = 14;
        const totalHeight = 6 * lineGap;
        const startY = center - totalHeight / 2 + 6;
        const y = startY + (5 - index) * lineGap;
        const x = center - HEXAGRAM_WIDTH / 2;

        const isVisible = index < visibleLines;
        const isPulsing = pulsingLine === index;

        const fillColor = isPulsing ? accentColor : "currentColor";
        const opacity = isVisible ? 1 : 0;

        const filter = isPulsing
            ? `drop-shadow(0 0 6px ${accentColor === "var(--accent-pink)" ? "#FF4FD8" : "#00C78B"})`
            : "none";

        const lineStyle = {
            opacity,
            filter,
            transition: prefersReducedMotion ? "none" : `opacity ${LINE_REVEAL_DURATION}ms ease-out, filter ${PULSE_DURATION}ms ease-in-out`,
        };

        if (isYang) {
            return (
                <rect
                    key={index}
                    x={x}
                    y={y}
                    width={HEXAGRAM_WIDTH}
                    height={lineHeight}
                    fill={fillColor}
                    rx={1}
                    style={lineStyle}
                />
            );
        } else {
            const segmentWidth = (HEXAGRAM_WIDTH - 12) / 2;
            return (
                <g key={index} style={lineStyle}>
                    <rect x={x} y={y} width={segmentWidth} height={lineHeight} fill={fillColor} rx={1} />
                    <rect x={x + HEXAGRAM_WIDTH - segmentWidth} y={y} width={segmentWidth} height={lineHeight} fill={fillColor} rx={1} />
                </g>
            );
        }
    };

    const currentOpacity = isFocused ? FOCUS_OPACITY : isHovered ? HOVER_OPACITY : DEFAULT_OPACITY;

    // Format keywords for display
    const keywordsText = Array.isArray(hexagram.keywords)
        ? hexagram.keywords.slice(0, 3).join(" · ")
        : hexagram.keywords;

    return (
        <div
            className="instrument-backdrop"
            onMouseEnter={() => setIsHovered(true)}
            onMouseLeave={() => setIsHovered(false)}
            onClick={handleClick}
            style={{
                position: "absolute",
                top: "50%",
                left: "50%",
                transform: `translate(-50%, -50%) scale(${INSTRUMENT_SCALE})`,
                zIndex: 1,
                cursor: "pointer",
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
            }}
        >
            <svg
                width={SVG_SIZE}
                height={SVG_SIZE}
                viewBox={`0 0 ${SVG_SIZE} ${SVG_SIZE}`}
                style={{
                    color: "var(--text-primary)",
                    opacity: currentOpacity,
                    transition: prefersReducedMotion ? "none" : "opacity 0.15s ease",
                }}
                aria-hidden="true"
            >
                {/* Hexagram lines only - no rings */}
                {lines.map((isYang, index) => renderHexagramLine(isYang, index))}
            </svg>

            {/* Metadata on hover - cleaner and more legible */}
            <div
                style={{
                    position: "absolute",
                    bottom: "-55px",
                    opacity: isHovered ? 1 : 0,
                    transition: prefersReducedMotion ? "none" : "opacity 0.15s ease",
                    textAlign: "center",
                    pointerEvents: "none",
                    userSelect: "none",
                    whiteSpace: "nowrap",
                }}
            >
                {/* Number and Name */}
                <div
                    style={{
                        fontSize: "15px",
                        color: "var(--text-primary)",
                        letterSpacing: "0.02em",
                        marginBottom: "4px",
                    }}
                >
                    <span
                        style={{
                            fontFamily: "source-code-pro, Menlo, Monaco, Consolas, monospace",
                            color: accentColor,
                            fontWeight: 600,
                            marginRight: "8px",
                        }}
                    >
                        {hexagram.number}
                    </span>
                    {hexagram.name_en}
                    <span
                        style={{
                            marginLeft: "8px",
                            fontSize: "14px",
                            opacity: 0.5,
                        }}
                    >
                        {hexagram.name_zh}
                    </span>
                </div>

                {/* Keywords */}
                <div
                    style={{
                        fontFamily: "source-code-pro, Menlo, Monaco, Consolas, monospace",
                        fontSize: "11px",
                        color: "var(--text-muted)",
                        letterSpacing: "0.05em",
                    }}
                >
                    {keywordsText}
                </div>
            </div>
        </div>
    );
}

// Export hexagram data for contextual lens
export function getHexagramInfo() {
    const hexagramNumber = getDailyHexagramNumber();
    const hexagram = hexagrams.find((h) => h.number === hexagramNumber) || hexagrams[0];
    const accentColor = hexagramNumber % 2 === 0 ? "var(--accent-green)" : "var(--accent-pink)";

    return {
        number: hexagramNumber,
        name_en: hexagram.name_en,
        name_zh: hexagram.name_zh,
        keywords: hexagram.keywords,
        accentColor,
    };
}

import React, { useMemo } from "react";
import { getDailyHexagramNumber, getDailyMode, getSeededRandoms } from "../data/vocabulary";

/**
 * TextCloud - Felipe's daily vocabulary words
 * 
 * Displays 6-8 words from the daily mode, positioned around the left panel.
 * Words are sparse, low opacity, and never overlap the name.
 * 
 * Configuration:
 * - WORD_COUNT: Number of words to display (6-10)
 * - BASE_OPACITY: Default word opacity
 * - HIGHLIGHT_OPACITY: Opacity for 1-2 highlighted words
 * - WORD_SIZE: Font size in rem
 */

// === CONFIGURATION ===
const WORD_COUNT = 8;              // Words to display from mode
const BASE_OPACITY = 0.12;         // Very subtle
const HIGHLIGHT_OPACITY = 0.4;     // Slightly more visible for highlighted
const WORD_SIZE = 0.9;             // rem
const HIGHLIGHT_COUNT = 2;         // Number of words to highlight with accent

export default function TextCloud() {
  // Get daily hexagram and mode
  const hexagramNumber = useMemo(() => getDailyHexagramNumber(), []);
  const dailyMode = useMemo(() => getDailyMode(hexagramNumber), [hexagramNumber]);

  // Accent color based on hexagram (even = green, odd = pink)
  const accentColor = hexagramNumber % 2 === 0
    ? "var(--accent-green, #00C78B)"
    : "var(--accent-pink, #FF4FD8)";

  // Get seeded random positions (stable for the day)
  const randomValues = useMemo(() => {
    return getSeededRandoms(WORD_COUNT * 3); // Need x, y, and selection randoms
  }, []);

  // Select words to display
  const wordsToShow = useMemo(() => {
    const words = [...dailyMode.words];
    // Shuffle deterministically using seeded randoms
    for (let i = words.length - 1; i > 0; i--) {
      const j = Math.floor(randomValues[i] * (i + 1));
      [words[i], words[j]] = [words[j], words[i]];
    }
    return words.slice(0, WORD_COUNT);
  }, [dailyMode.words, randomValues]);

  // Determine which words to highlight (first HIGHLIGHT_COUNT based on seed)
  const highlightedIndices = useMemo(() => {
    const indices = new Set();
    for (let i = 0; i < HIGHLIGHT_COUNT && i < wordsToShow.length; i++) {
      indices.add(Math.floor(randomValues[WORD_COUNT + i] * wordsToShow.length) % wordsToShow.length);
    }
    return indices;
  }, [randomValues, wordsToShow.length]);

  return (
    <div
      style={{
        position: "absolute",
        top: 0,
        left: 0,
        width: "100%",
        height: "100%",
        pointerEvents: "none",
        overflow: "hidden",
      }}
    >
      {wordsToShow.map((word, i) => {
        // Position words in the margins, avoiding center (where name is)
        // Use zones: top, bottom, left edge, right edge of the left panel
        const zone = i % 4;
        let top, left;

        switch (zone) {
          case 0: // Top area
            top = 8 + randomValues[i] * 15; // 8-23%
            left = 10 + randomValues[i + WORD_COUNT] * 35; // 10-45%
            break;
          case 1: // Bottom area
            top = 75 + randomValues[i] * 18; // 75-93%
            left = 10 + randomValues[i + WORD_COUNT] * 35;
            break;
          case 2: // Left edge
            top = 25 + randomValues[i] * 50; // 25-75%
            left = 3 + randomValues[i + WORD_COUNT] * 12; // 3-15%
            break;
          case 3: // Right edge (but still in left panel)
          default:
            top = 25 + randomValues[i] * 50;
            left = 38 + randomValues[i + WORD_COUNT] * 10; // 38-48%
            break;
        }

        const isHighlighted = highlightedIndices.has(i);

        return (
          <span
            key={`${word}-${i}`}
            style={{
              position: "absolute",
              top: `${top}%`,
              left: `${left}%`,
              fontSize: `${WORD_SIZE}rem`,
              fontFamily: "inherit",
              color: isHighlighted ? accentColor : "rgba(0,0,0,1)",
              opacity: isHighlighted ? HIGHLIGHT_OPACITY : BASE_OPACITY,
              whiteSpace: "nowrap",
              userSelect: "none",
              letterSpacing: "0.02em",
            }}
          >
            {word}
          </span>
        );
      })}
    </div>
  );
}

import React, { useState } from "react";
import workData from "../data/workData.json";

export default function Work() {
  const [currentWork, setCurrentWork] = useState(workData.data[0].name);

  return (
    <div className="flex flex-wrap justify-center items-center">
      {/* Project titles - clickable list */}
      <div className="pb-8 px-5 flex flex-wrap justify-center items-center">
        {workData.data.map((work, key) => (
          <h3
            key={key}
            className="cursor-pointer px-5 py-1 text-lg"
            style={{
              color:
                work.name === currentWork
                  ? "var(--text-primary)"
                  : "var(--text-muted)",
            }}
            onClick={() => setCurrentWork(work.name)}
          >
            {work.name}
          </h3>
        ))}
      </div>

      {/* Selected project display */}
      {workData.data
        .filter((work) => work.name === currentWork)
        .map((work, key) => (
          <div key={key} className="flex justify-center items-center flex-col">
            <a
              className="flex justify-center items-center flex-col"
              href={work.externalLink}
              target="_blank"
              rel="noreferrer"
            >
              <img
                src={work.media[0].link}
                alt={work.name}
