import React, { useState } from "react";
import { Link } from "react-router-dom";
import { getShelves } from "../data/siteContent";
import MobileNav from "../components/MobileNav";

/**
 * Reading Page - Goodreads-style book display with vertical cards
 * 
 * Features:
 * - Sections by shelf (Currently Reading, Up Next, Finished, Reference)
 * - Vertical book cards with prominent covers
 * - Fallback placeholder for missing/broken images
 * - External links to Goodreads
 */

export default function Reading() {
  const shelves = getShelves();

  return (
    <div
      className="min-h-screen py-8 pb-32"
      style={{ backgroundColor: "var(--bg-primary)" }}
    >
      {/* Site mark */}
      {/* Mobile: Relative flow. Desktop: Fixed top-left. */}
      <Link
        to="/"
        className="relative block mb-8 px-8 pt-12 md:fixed md:top-12 md:left-12 md:m-0 md:p-0 z-50 font-serif text-pink tracking-tight hover:opacity-80 transition-opacity leading-none text-center md:text-left"
        style={{ fontSize: "48px" }}
      >
        Felipe Waldeck
      </Link>

      {/* Header */}
      <div className="px-8 md:px-32 pt-0 md:pt-12">
        <h1
          className="text-3xl md:text-5xl text-center pt-4 font-serif text-pink"
        >
          Reading
        </h1>
      </div>

      {/* Shelves */}
      <div className="px-8 md:px-16 lg:px-32 pt-8">
        {shelves.map((shelf) => (
          <section key={shelf.key} className="mb-16">
            {/* Shelf header */}
            <h2
              className="text-xl md:text-2xl mb-8 pb-2"
              style={{
                color: "var(--text-primary)",
                borderBottom: "1px solid var(--border-subtle)",
              }}
            >
              {shelf.displayName}
              <span
                className="ml-3 text-sm"
                style={{
                  color: "var(--text-muted)",
                  fontFamily: "source-code-pro, Menlo, Monaco, Consolas, monospace",
                }}
              >
                {shelf.books.length}
              </span>
            </h2>

            {/* Book grid - vertical cards */}
            <div
              className="grid gap-6"
              style={{
                gridTemplateColumns: "repeat(auto-fill, minmax(140px, 1fr))",
              }}
            >
              {shelf.books.map((book) => (
                <BookCard key={book.id} book={book} />
              ))}
            </div>

            {shelf.books.length === 0 && (
              <p style={{ color: "var(--text-muted)" }}>No books in this shelf.</p>
            )}
          </section>
        ))}
      </div>

      {/* Footer note */}
      <div className="px-8 md:px-32 pt-8 pb-16">
        <p
          className="text-center text-sm"
          style={{
            color: "var(--text-muted)",
            fontFamily: "source-code-pro, Menlo, Monaco, Consolas, monospace",
          }}
        >
          Data synced from Goodreads
        </p>
      </div>

      <MobileNav activeTab="READING" />
    </div>
  );
}

/**
 * BookCard - Vertical book card with cover and fallback
 */
function BookCard({ book }) {
  const [imageError, setImageError] = useState(false);
  const [imageLoaded, setImageLoaded] = useState(false);

  const handleClick = () => {
    if (book.link) {
      window.open(book.link, "_blank", "noopener,noreferrer");
    }
  };

  // Determine if we should show placeholder
  const showPlaceholder = !book.cover || imageError;

  return (
    <div
      onClick={handleClick}
      className="flex flex-col cursor-pointer group"
      style={{ transition: "transform 0.15s ease" }}
      onMouseEnter={(e) => {
        e.currentTarget.style.transform = "translateY(-4px)";
      }}
      onMouseLeave={(e) => {
        e.currentTarget.style.transform = "translateY(0)";
      }}
    >
      {/* Cover container */}
      <div
        className="relative w-full mb-3"
        style={{
          aspectRatio: "2/3",
          backgroundColor: "var(--bg-secondary)",
          borderRadius: "4px",
          overflow: "hidden",
          border: "1px solid var(--border-subtle)",
          transition: "border-color 0.15s ease, box-shadow 0.15s ease",
        }}
        onMouseEnter={(e) => {
          e.currentTarget.style.borderColor = "var(--accent-green)";
          e.currentTarget.style.boxShadow = "0 8px 24px rgba(0,0,0,0.4)";
        }}
        onMouseLeave={(e) => {
          e.currentTarget.style.borderColor = "var(--border-subtle)";
          e.currentTarget.style.boxShadow = "none";
        }}
      >
        {/* Actual image (hidden if error/not loaded) */}
        {book.cover && !imageError && (
          <img
            src={book.cover}
            alt={book.title}
            onError={() => setImageError(true)}
            onLoad={() => setImageLoaded(true)}
            style={{
              width: "100%",
              height: "100%",
              objectFit: "cover",
              opacity: imageLoaded ? 1 : 0,
              transition: "opacity 0.2s ease",
            }}
          />
        )}

        {/* Placeholder - shown while loading or on error */}
        {(showPlaceholder || !imageLoaded) && (
          <div
            className="absolute inset-0 flex flex-col items-center justify-center p-3"
            style={{
              backgroundColor: "var(--bg-secondary)",
              opacity: imageLoaded && !showPlaceholder ? 0 : 1,
              transition: "opacity 0.2s ease",
            }}
          >
            {/* Book icon placeholder */}
            <svg
              width="32"
              height="32"
              viewBox="0 0 24 24"
              fill="none"
              stroke="var(--text-muted)"
              strokeWidth="1"
              className="mb-2"
              style={{ opacity: 0.4 }}
            >
              <path d="M4 19.5A2.5 2.5 0 0 1 6.5 17H20" />
              <path d="M6.5 2H20v20H6.5A2.5 2.5 0 0 1 4 19.5v-15A2.5 2.5 0 0 1 6.5 2z" />
            </svg>
            <div
              style={{
                color: "var(--text-muted)",
                fontSize: "10px",
                textAlign: "center",
                lineHeight: 1.3,
                display: "-webkit-box",
                WebkitLineClamp: 3,
                WebkitBoxOrient: "vertical",
                overflow: "hidden",
              }}
            >
              {book.title}
            </div>
          </div>
        )}

        {/* External link indicator */}
        {book.link && (
          <div
            className="absolute top-2 right-2 opacity-0 group-hover:opacity-100"
            style={{
              backgroundColor: "rgba(0,0,0,0.7)",
              borderRadius: "4px",
              padding: "4px",
              transition: "opacity 0.15s ease",
            }}
          >
            <svg
              width="12"
              height="12"
              viewBox="0 0 24 24"
              fill="none"
              stroke="var(--text-primary)"
              strokeWidth="2"
            >
              <path d="M18 13v6a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2V8a2 2 0 0 1 2-2h6" />
              <polyline points="15 3 21 3 21 9" />
              <line x1="10" y1="14" x2="21" y2="3" />
            </svg>
          </div>
        )}
      </div>

      {/* Book info */}
      <div className="px-1">
        <h3
          className="font-bold text-sm leading-tight mb-1"
          style={{
            color: "var(--text-primary)",
            display: "-webkit-box",
            WebkitLineClamp: 2,
            WebkitBoxOrient: "vertical",
            overflow: "hidden",
          }}
          title={book.title}
        >
          {book.title}
        </h3>
        <p
          className="text-xs"
          style={{
            color: "var(--text-secondary)",
            whiteSpace: "nowrap",
            overflow: "hidden",
            textOverflow: "ellipsis",
          }}
        >
          {book.author}
        </p>
      </div>
    </div>
  );
}

import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation } from "swiper/modules";
import Zoom from "react-medium-image-zoom";
import "../styles/Zoom.css";
// Import Swiper styles
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import renderDataJson from "../data/renderData.json";
const renderData = renderDataJson.data;

export default function Renders() {
  return (
    <div className="min-min-h-screen py-8">
      <div className="px-8 md:px-32">
        <a href="/" className="text-lg md:text-2xl px-2 md:px-0">
          Home
        </a>
        {/* <div className="w-full"> */}
        <h1 className="text-3xl md:text-5xl text-center px-2 pt-4 md:px-0">
          Miscellaneous Projects
        </h1>
      </div>
      {renderData.map((render, key) => (
        <div key={key} id={render.id}>
          <div className="flex flex-col justify-center mt-10  text-center">
            <h2 className="text-xl md:text-3xl">{render.name}</h2>
            <h3 className="text-lg md:text-2xl">{render.date}</h3>
          </div>

          <Swiper
            breakpoints={{
              // when window width is >= 768px
              768: {
                slidesPerView: 3,
              },
            }}
            style={{ padding: "2rem 0 0.5rem 0", height: "100%", width: "90%" }}
            navigation={true}
            slidesPerView={1}
            spaceBetween={30}
            modules={[Navigation]}
            className="mySwiper h-1/2"
          >
            {render.media.map((media, key) =>
              media.type === "image" ? (
                <SwiperSlide
                  style={{ display: "flex" }}
                  className="flex flex-col justify-center items-center px-0 "
                  key={key}
                >
                  <Zoom style={{ maxHeight: "100%", objectFit: "contain" }}>
                    <img
                      style={{ maxHeight: "100%", objectFit: "contain" }}
                      src={media.link}
                      alt={media.caption}
                      loading="lazy"
                      className="drop-shadow-md hover:drop-shadow-xl"
                    ></img>
                  </Zoom>
                  <p className="italic px-2 pt-2 text-center">
                    {media.caption}
                  </p>
                </SwiperSlide>
              ) : (
                <SwiperSlide
                  style={{ display: "flex" }}
                  className="flex justify-center items-center  flex-col"
                  key={key}
                >
                  {/* <YouTubeEmbed link={media.link}></YouTubeEmbed> */}
                  <iframe
                    style={{ width: "100%" }}
                    width="853"
                    height="480"
                    src={media.link}
                    frameBorder="0"
                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                    allowFullScreen
                    title="Embedded youtube"
                  />
                  <p className="italic px-2 pt-2 text-center">
                    {media.sourceLink ? (
                      <a
                        href={media.sourceLink}
                        target="_blank"
                        rel="noreferrer"
                        className="underline underline-offset-4"
                      >
                        {media.caption}
                      </a>
                    ) : (
                      media.caption
                    )}
                  </p>
                </SwiperSlide>
              )
            )}
          </Swiper>
          <div className="flex justify-center pt-4">
            <div className="md:w-1/2 w-5/6 text-lg">
              {render.sourceCode ? (
                <a
                  href={render.sourceCode}
                  target="_blank"
                  rel="noreferrer"
                  className="font-bold underline underline-offset-4 py-4"
                >
                  Project Files
                </a>
              ) : null}
              {"    "}
              {render.liveLink ? (
                <a
                  href={render.liveLink}
                  target="_blank"
                  rel="noreferrer"
                  className="font-bold underline underline-offset-4 py-4"
                >
                  Project Link
                </a>
              ) : null}
              <div>{render.description}</div>
              <p className="py-4">
                <span className="font-bold">Made Using: </span>{" "}
                {render.technology}
              </p>
            </div>
          </div>
        </div>
      ))}
    </div>
  );
}

